import { forwardRef, useImperativeHandle, useCallback } from "react";
import type { AnimatedIconHandle, AnimatedIconProps } from "./types";
import { motion, useAnimate, stagger } from "motion/react";

const Alipay = forwardRef<AnimatedIconHandle, AnimatedIconProps>(
  (
    { size = 24, color = "currentColor", strokeWidth = 2, className = "" },
    ref,
  ) => {
    const [scope, animate] = useAnimate();

    const start = useCallback(async () => {
      await Promise.all([
        animate(".zhi-path", { pathLength: 0, opacity: 0 }, { duration: 0 }),
        animate(".zhi-group", { scale: 1 }, { duration: 0 }),
      ]);

      await animate(
        ".zhi-path",
        {
          pathLength: [0, 1],
          opacity: [0, 1],
        },
        {
          duration: 0.6,
          ease: "easeInOut",
          delay: stagger(0.1),
        },
      );

      await animate(
        ".zhi-group",
        { scale: 1.15 },
        { duration: 0.2, ease: "easeOut" },
      );

      await animate(
        ".zhi-group",
        { scale: 1 },
        { duration: 0.2, ease: "easeIn" },
      );
    }, [animate]);

    const stop = useCallback(() => {
      animate(".zhi-path", { pathLength: 1, opacity: 1 }, { duration: 0.2 });
      animate(".zhi-group", { scale: 1 }, { duration: 0.2 });
    }, [animate]);

    useImperativeHandle(ref, () => ({
      startAnimation: start,
      stopAnimation: stop,
    }));

    return (
      <div
        className={`inline-flex items-center justify-center ${className}`}
        onMouseEnter={start}
        onMouseLeave={stop}
      >
        <motion.svg
          ref={scope}
          xmlns="http://www.w3.org/2000/svg"
          width={size}
          height={size}
          viewBox="0 0 24 24"
          fill="none"
          stroke={color}
          strokeWidth={strokeWidth}
          strokeLinecap="round"
          strokeLinejoin="round"
          className="cursor-pointer"
          style={{ overflow: "visible" }}
        >
          <path d="M0 0h24v24H0z" stroke="none" fill="none" />

          <path d="M19 3h-14a2 2 0 0 0 -2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2 -2v-14a2 2 0 0 0 -2 -2" />

          <motion.g className="zhi-group origin-center">
            <path className="zhi-path" d="M7 7h10" />

            <path className="zhi-path" d="M12 3v7" />

            <path
              className="zhi-path"
              d="M21 17.314c-2.971 -1.923 -15 -8.779 -15 -1.864c0 1.716 1.52 2.55 2.985 2.55c3.512 0 6.814 -5.425 6.814 -8h-6.604"
            />
          </motion.g>
        </motion.svg>
      </div>
    );
  },
);

Alipay.displayName = "Alipay";

export default Alipay;

export interface AnimatedIconProps {
  /** Icon size in pixels or CSS string */
  size?: number | string;
  /** Icon color (defaults to currentColor) */
  color?: string;
  /** SVG stroke width */
  strokeWidth?: number;
  /** Additional CSS classes */
  className?: string;
}

export interface AnimatedIconHandle {
  startAnimation: () => void;
  stopAnimation: () => void;
}
